property.value:

let $modificationOps := (
		'+=', '-=', '*=', '/=', '%=', '<<=', '>>=', '>>>=',
		'&amp;=', '^=', '|='
	),
	$incrementOps := ('++', '--')
return //VariableDeclaration[
	not(ModifierNode[@Final = true()])
	and not(ancestor::*[ModifierNode[@Final = true()]])
	and not(ancestor::Parameter)
	and not(ancestor::ForLoopStatement)
	and not(ancestor::ForEachStatement)
	and ancestor::Method
	and (
		let $varName := VariableExpression/@Image,
			$method := ancestor::Method[1],
			$beginLine := @BeginLine
		return not(
			$method//AssignmentExpression[
				.//VariableExpression/@Image = $varName
				and @BeginLine > $beginLine
			]
			or $method//UnaryExpression[
				.//VariableExpression/@Image = $varName
				and @BeginLine > $beginLine
			]
			or $method//BinaryExpression[
				@Op = $modificationOps
				and .//VariableExpression/@Image = $varName
				and @BeginLine > $beginLine
			]
			or $method//*[
				(@Op = $incrementOps
				or local-name() = ('PostfixExpression', 'PrefixExpression'))
				and .//VariableExpression/@Image = $varName
				and @BeginLine > $beginLine
			]
		)
	)
]

rule.example:

// Violation: Variable never reassigned but not declared final
public void method() {
    Integer value = 5;  // ❌ Should be final
    String name = 'test';  // ❌ Should be final
    // value and name are never reassigned
}

// Valid: Variables declared as final
public void method() {
    final Integer value = 5;  // ✅
    final String name = 'test';  // ✅
    // value and name are never reassigned
}


// Valid: Variable that is reassigned (cannot be final)
public void method() {
    Integer value = 5;
    value = 10;  // Reassigned, so cannot be final
}

// Valid: Variable with increment/decrement (cannot be final)
public void method() {
    Integer count = 0;
    count++;  // Modified, so cannot be final
}


// Valid: Loop variables are excluded (cannot be final)
public void method() {
    for (Integer i = 0; i < 10; i++) {
        // Loop variable 'i' is excluded
    }
}

// Valid: Parameters are excluded (cannot be final in this context)
public void method(Integer param) {
    // Parameter 'param' is excluded
}
