rule.example:

// Violation: List with 2+ items on single line
List<String> items = new List<String>{ 'item1', 'item2', 'item3' };

// Valid: List with 2+ items on multiple lines
List<String> items = new List<String>{
    'item1',
    'item2',
    'item3'
};


// Valid: Single item can be on one line
List<String> items = new List<String>{ 'item1' };

// Valid: Empty list can be on one line
List<String> items = new List<String>();


// Violation: List of Integers on single line
List<Integer> numbers = new List<Integer>{ 1, 2, 3, 4 };

// Valid: List of Integers on multiple lines
List<Integer> numbers = new List<Integer>{
    1,
    2,
    3,
    4
};
