rule.example:

// Violation: Map with 2+ entries on single line
Map<String, Integer> map = new Map<String, Integer>{ 'key1' => 1, 'key2' => 2, 'key3' => 3 };

// Valid: Map with 2+ entries on multiple lines
Map<String, Integer> map = new Map<String, Integer>{
    'key1' => 1,
    'key2' => 2,
    'key3' => 3
};


// Valid: Single entry can be on one line
Map<String, Integer> map = new Map<String, Integer>{ 'key1' => 1 };

// Valid: Empty map can be on one line
Map<String, Integer> map = new Map<String, Integer>();


// Violation: Map with String keys and String values on single line
Map<String, String> config = new Map<String, String>{ 'env' => 'prod', 'region' => 'us-east' };

// Valid: Map with String keys and String values on multiple lines
Map<String, String> config = new Map<String, String>{
    'env' => 'prod',
    'region' => 'us-east'
};
