rule.example:

// Violation: Multiple contains() calls with OR
if (str.contains('a') || str.contains('b') || str.contains('c')) {
    // ...
}

// Valid: Use regex pattern instead
if (Pattern.matches('.*(a|b|c).*', str)) {
    // ...
}


// Violation: Multiple contains() calls with AND
if (str.contains('prefix') &amp;&amp; str.contains('suffix')) {
    // ...
}

// Valid: Use regex pattern instead
if (Pattern.matches('.*prefix.*suffix.*', str)) {
    // ...
}


// Valid: Single contains() call is allowed
if (str.contains('test')) {
    // ...
}

// Valid: Contains() calls in different conditionals are allowed
if (str.contains('a')) {
    // ...
}
if (str.contains('b')) {
    // ...
}
