property.value:

//AssignmentExpression[
	@Op = '='
	and VariableExpression
	and MethodCallExpression
	and MethodCallExpression/ReferenceExpression[1]
	and (
		let $receiverVar := MethodCallExpression/ReferenceExpression[1]/@Image,
			$assign := .,
			$method := ancestor::Method[1]
		return exists($method//VariableDeclarationStatements[
			VariableDeclaration/VariableExpression[@Image = $receiverVar]
			and . << $assign
		])
	)
	and not(ancestor::Method[@Constructor = true()])
]

rule.example:

// Violation: Multiple assignments to same variable
Builder builder = new Builder();
builder = builder.setName('test');
builder = builder.setValue(5);

// Valid: Use method chaining with final variable
final Builder builder = new Builder()
	.setName('test')
	.setValue(5);
