rule.description:
When using String.join() with an empty string separator, use string concatenation instead:

      Instead of: String.join('', new List < String > {'a', 'b', 'c'});
      Use: 'a' + 'b' + 'c';

property.value:

//MethodCallExpression[
    @Image = 'join'
    and ancestor::PrimaryExpression[1]/ReferenceExpression[@Image = 'String']
    and .//LiteralExpression[@String = true() and (@Image = "''" or @Image = '""' or string-length(@Image) = 0)]
    and .//NewListLiteralExpression
]
