rule.description:
When using String.join() with an empty string separator, use string
concatenation instead for better readability and simplicity.

Instead of: String.join('', new List < String > {'a', 'b', 'c'});
Use: 'a' + 'b' + 'c';

Version: 1.0.1

property.value:

let $isEmptyString := function($expr) {
	$expr/@String = true()
		and (
			$expr/@Image = "''"
			or $expr/@Image = '""'
			or string-length($expr/@Image) = 0
		)
}
return //MethodCallExpression[
	@MethodName = 'join'
	and @FullMethodName = 'String.join'
	and .//NewListLiteralExpression
	and (
		$isEmptyString(./LiteralExpression)
		or $isEmptyString(
			./NewListLiteralExpression
			/following-sibling::LiteralExpression
		)
	)
]

rule.example:

// Violation: String.join with empty separator
String result = String.join('', new List<String> {'a', 'b', 'c'});
String text = String.join('', new List<String> {'Hello', 'World'});

// Valid: Use string concatenation
String result = 'a' + 'b' + 'c';
String text = 'Hello' + 'World';


// Violation: Empty separator with multiple strings
String path = String.join('', new List<String> {'/api', '/v1', '/users'});

// Valid: Use string concatenation
String path = '/api' + '/v1' + '/users';

// Valid: String.join with non-empty separator (not flagged)
String result = String.join(',', new List<String> {'a', 'b', 'c'});
String text = String.join('\n', new List<String> {'line1', 'line2'});
