property.value:

//LiteralExpression[
	@String = true()
	and contains(@Image, '\n')
	and contains(substring-after(@Image, '\n'), '\n')
	and not(ancestor::MethodCallExpression[@MethodName = 'join'])
]

rule.example:

// Violation: Multiple newlines in string literal
String text = 'line1\nline2\nline3';

// Valid: Use String.join()
String text = String.join(new List<String> {'line1', 'line2', 'line3'}, '\n');
