rule.description:
When using String.join() with an empty string separator and all strings in the list
except the last one end with the same non-empty sequence of characters, use the
common suffix as the separator instead:

Instead of: String.join('', new List<String>{'line1\n', 'line2\n', 'last'});
Use: String.join('\n', new List<String>{'line1', 'line2', 'last'});

property.value:

//MethodCallExpression[
    @MethodName = 'join' and
    @FullMethodName = 'String.join' and
    count(*[local-name() = 'LiteralExpression' or local-name() = 'NewListLiteralExpression']) >= 2
][
    (
        (*[2][self::LiteralExpression[@LiteralType = 'STRING' and @Image = '']] and
         *[3][self::NewListLiteralExpression]) or
        (*[2][self::NewListLiteralExpression] and
         *[3][self::LiteralExpression[@LiteralType = 'STRING' and @Image = '']])
    )
][
    let $strings := if (*[2][self::NewListLiteralExpression])
                    then *[2]//LiteralExpression[@LiteralType = 'STRING']/@Image
                    else *[3]//LiteralExpression[@LiteralType = 'STRING']/@Image,
         $stringCount := count($strings)
    return
    $stringCount >= 3 and
    (
        let $lastString := $strings[$stringCount],
             $otherStrings := $strings[position() < $stringCount],
             $suffixes := for $s in $otherStrings return
               if (string-length($s) > 0) then
                 substring($s, string-length($s), 1)
               else ''
        return
        count(distinct-values($suffixes)) = 1 and
        string-length($suffixes[1]) > 0 and
        not(ends-with($lastString, $suffixes[1]))
    )
]
