property.value:

for $var in //VariableDeclaration[VariableExpression]
return if (
	let $varNameLower := lower-case($var/@Image)
	return (
		string-length($varNameLower) > 1
		and $var/ancestor::ApexFile//UserClass[
			lower-case(@Image) = $varNameLower
			or lower-case(@SimpleName) = $varNameLower
		]
	)
) then $var else ()

rule.example:

// Violation: Variable name matches class name (case-insensitive)
public class Account {
    // ...
}

public void method() {
    Account Account = new Account();  // ❌ Variable name matches class (exact case)
    Account ACCOUNT = new Account();  // ❌ Variable name matches class (uppercase)
    Account account = new Account();  // ❌ Variable name matches class (lowercase)
    Account AcCoUnT = new Account();  // ❌ Variable name matches class (mixed case)
}

// Valid: Use different variable name
public void method() {
    Account acc = new Account();  // ✅ Different name (abbreviation)
    Account accountInstance = new Account();  // ✅ Different name (with suffix)
    Account accountBob = new Account();  // ✅ Different name (with suffix)
    Account myAccount = new Account();  // ✅ Different name (with prefix)
    Account accounts = new Account();  // ✅ Valid: Plural is allowed
    Account r = new Account();  // ✅ Valid: Single letter is allowed
}


// Violation: Variable name matches inner class name
public class Outer {
    public class Inner {
        // ...
    }

    public void method() {
        Inner Inner = new Inner();  // ❌ Variable name matches inner class (exact case)
        Inner inner = new Inner();  // ❌ Variable name matches inner class (lowercase)
        Inner INNER = new Inner();  // ❌ Variable name matches inner class (uppercase)
    }
}

// Valid: Use different variable name
public void method() {
    Inner innerInstance = new Inner();  // ✅ Different name (with suffix)
    Inner innerBob = new Inner();  // ✅ Different name (with suffix)
    Inner myInner = new Inner();  // ✅ Different name (with prefix)
    Inner item = new Inner();  // ✅ Different name (generic)
}


// Violation: Multi-word class names also checked
public class TestHelper {
    // ...
}

public void method() {
    TestHelper TestHelper = new TestHelper();  // ❌ Variable name matches class
    TestHelper testHelper = new TestHelper();  // ❌ Variable name matches class (case-insensitive)
    TestHelper TESTHELPER = new TestHelper();  // ❌ Variable name matches class (uppercase)
}

// Valid: Different variable names
public void method() {
    TestHelper helper = new TestHelper();  // ✅ Different name
    TestHelper testHelperInstance = new TestHelper();  // ✅ Different name (with suffix)
    TestHelper myTestHelper = new TestHelper();  // ✅ Different name (with prefix)
}


// Valid: Partial matches are allowed (variable contains class name but is different)
public class Account {
    public void method() {
        String accountName = 'Test';  // ✅ Valid: accountName != Account
        String accountId = '123';  // ✅ Valid: accountId != Account
        Account accountInstance = new Account();  // ✅ Valid: accountInstance != Account
        Account accounts = new Account();  // ✅ Valid: Plural is allowed
    }
}

public class Request {
    public void method() {
        Request requestItem = new Request();  // ✅ Valid: Contains class name but is longer
        Request request = new Request();  // ❌ Violation: Exact match
        Request r = new Request();  // ✅ Valid: Single letter is allowed
    }
}

public class Quote {
    public void method() {
        Quote quotes = new Quote();  // ✅ Valid: Plural is allowed
        Quote quote = new Quote();  // ❌ Violation: Exact match
    }
}

public class Dress {
    public void method() {
        Dress dresses = new Dress();  // ✅ Valid: Plural is allowed
        Dress myDress = new Dress();  // ✅ Valid: Contains class name with prefix
        Dress dressItem = new Dress();  // ✅ Valid: Contains class name with suffix
        Dress redDressObject = new Dress();  // ✅ Valid: Contains class name in middle
        Dress dress = new Dress();  // ❌ Violation: Exact match
    }
}
