rule.example:

// Violation: Enum with only 2 values
public enum Status {
    ACTIVE,
    INACTIVE
}

// Valid: Enum with 3 or more values
public enum Status {
    ACTIVE,
    INACTIVE,
    PENDING
}


// Violation: Enum with only 1 value
public enum Direction {
    NORTH
}

// Valid: Enum with 3 or more values
public enum Direction {
    NORTH,
    SOUTH,
    EAST,
    WEST
}


// Valid: Enum with 3 values (meets minimum)
public enum Priority {
    LOW,
    MEDIUM,
    HIGH
}

// Valid: Enum with 4+ values
public enum LogLevel {
    DEBUG,
    INFO,
    WARN,
    ERROR,
    FATAL
}
