rule.example:

// Violation: Static field in inner class (not allowed in Apex)
public class Outer {
    public class Inner {
        public static Integer value;  // ❌
    }
}

// Valid: Non-static field in inner class
public class Outer {
    public class Inner {
        public Integer value;  // ✅
    }
}


// Violation: Static method in inner class (not allowed in Apex)
public class Outer {
    public class Inner {
        public static void method() { }  // ❌
    }
}

// Valid: Non-static method in inner class
public class Outer {
    public class Inner {
        public void method() { }  // ✅
    }
}
