rule.example:

// Violation: Method throws exception but no @throws documentation
/**
 * Processes the account.
 */
public void processAccount(Account acc) {
	if (acc == null) {
		throw new IllegalArgumentException('Account cannot be null');
	}
}

// Valid: Method with @throws documentation
/**
 * Processes the account.
 * @throws IllegalArgumentException if account is null
 */
public void processAccount(Account acc) {
	if (acc == null) {
		throw new IllegalArgumentException('Account cannot be null');
	}
}
