property.value:

//FormalComment[
	starts-with(@Image, '/**')
	and ends-with(@Image, '*/')
	and (
		(@BeginLine = @EndLine and (not(starts-with(substring-after(@Image, '/**'), ' ')) or not(ends-with(substring-before(@Image, '*/'), ' '))))
		or (@BeginLine != @EndLine and contains(@Image, ' * ') and not(contains(substring-after(@Image, ' * '), ' * ')))
	)
]

rule.example:

// Violation: Missing spaces in single-line comment
/**comment*/
public void method() {
}

// Valid: Properly formatted single-line comment
/** Comment */
public void method() {
}

// Valid: Multi-line comment when needed
/**
 * Longer comment that requires
 * multiple lines.
 */
public void method() {
}
