property.value:

//FormalComment[
    starts-with(@Image, '/**')
    and ends-with(@Image, '*/')
    and (
        (
            @BeginLine = @EndLine
            and (
                not(starts-with(substring-after(@Image, '/**'), ' '))
                or not(ends-with(substring-before(@Image, '*/'), ' '))
            )
        )
        or
        @BeginLine != @EndLine
            and contains(@Image, ' * ')
            and not(contains(substring-after(@Image, ' * '), ' * '))
    )
]
