property.value:

//FormalComment[
	starts-with(@Image, '/**')
	and ends-with(@Image, '*/')
	and (
		(
			@BeginLine = @EndLine
			and (
				not(starts-with(substring-after(@Image, '/**'), ' '))
				or not(ends-with(substring-before(@Image, '*/'), ' '))
			)
		)
		or (
			@BeginLine != @EndLine
			and contains(@Image, ' * ')
			and not(contains(substring-after(@Image, ' * '), ' * '))
		)
	)
]

rule.example:

// Violation: Missing spaces in single-line comment
/**comment*/
public void method() {
}

// Violation: Missing space after /**
/**comment */
public void method() {
}

// Violation: Missing space before */
/** comment*/
public void method() {
}

// Valid: Properly formatted single-line comment
/** Comment */
public void method() {
}

// Valid: Multi-line comment when needed
/**
 * Longer comment that requires
 * multiple lines to explain
 * complex functionality.
 */
public void method() {
}


// Valid: Single-line with proper spacing
/** Returns the account name */
public String getName() {
	return name;
}

// Valid: Multi-line for complex documentation
/**
 * Processes the account with validation.
 * This method performs multiple checks
 * and updates the account status.
 */
public void processAccount(Account acc) {
	// ...
}

// Valid: Multi-line with parameters
/**
 * Calculates the total value.
 * @param items List of items to calculate
 * @return Total value as Decimal
 */
public Decimal calculateTotal(List<Item> items) {
	// ...
}
