property.value:

//FormalComment[
	starts-with(@Image, '/**')
	and contains(@Image, '@group')
	and not(
		matches(@Image, '@group\s+(Class|Interface|Enum|Method|Property|Variable)(\s|\*/|$)')
	)
]

rule.example:

// Violation: Invalid @group value
/**
 * @group InvalidValue
 */
public class MyClass {
}

// Violation: @group with invalid value
/**
 * @group Custom
 */
public interface MyInterface {
}

// Valid: @group with Class value
/**
 * @group Class
 */
public class MyClass {
}

// Valid: @group with Interface value
/**
 * @group Interface
 */
public interface MyInterface {
}

// Valid: @group with Enum value
/**
 * @group Enum
 */
public enum MyEnum {
	VALUE1,
	VALUE2
}

// Valid: @group with Method value
/**
 * @group Method
 */
public void myMethod() {
}

// Valid: @group with Property value
/**
 * @group Property
 */
public String myProperty { get; set; }

// Valid: @group with Variable value
/**
 * @group Variable
 */
public String myVariable;


// Valid: No @group tag (not flagged)
/**
 * Class description
 */
public class MyClass {
}

// Valid: @group with valid value and additional text
/**
 * @group Class
 * Additional description
 */
public class MyClass {
}
