property.value:

//UserClass[
	.//Annotation[(@Image = 'IsTest' or @Name = 'IsTest')]
	and not(.//Annotation[(@Image = 'IsTest' or @Name = 'IsTest')]/AnnotationParameter[@Name = 'IsParallel' and (@Value = 'true' or @Image = 'true')])
]

rule.example:

// Violation: Test class without IsParallel=true
@IsTest
private class MyTestClass {
}

// Valid: Test class with IsParallel=true
@IsTest(IsParallel=true)
private class MyTestClass {
}
