rule.example:

// Violation: Inner class with underscore
public class Outer {
    public class Inner_Class {  // ❌
    }
}

// Violation: Inner class with multiple words (PascalCase)
public class Outer {
    public class InnerClass {  // ❌
    }
}

// Valid: Single word inner class
public class Outer {
    public class Inner {  // ✅
    }
}


// Violation: Inner class with multiple words
public class Outer {
    public class DataProcessor {  // ❌ Multiple words
    }
    public class User_Manager {  // ❌ Underscore
    }
}

// Valid: Single word inner classes
public class Outer {
    public class Processor {  // ✅
    }
    public class Manager {  // ✅
    }
}
