property.value:
//VariableDeclaration[
	VariableExpression[
		some $abbr in tokenize(
			if ('${disallowedAbbreviations}' = '${disallowedAbbreviations}')
			then 'acc,addr,attr,calc,cfg,col,con,ctx,curr,desc,dest,doc,dst,elem,fmt,hdr,idx,impl,init,lbl,len,mgr,msg,opp,opt,org,param,pos,prev,ref,repo,req,res,resp,spec,src,svc,util,val'
			else '${disallowedAbbreviations}',
			','
		)
			satisfies @Image = normalize-space($abbr)
		and not(
			some $suffix in tokenize(if ('${allowedSuffixes}' = '${allowedSuffixes}') then 'Api,Html,Id,Url' else '${allowedSuffixes}', ',') satisfies matches(@Image, concat($suffix, '$'))
		)
		and not(
			('${allowedPrefixes}' != '${allowedPrefixes}' 
			and string-length('${allowedPrefixes}') > 0 
			and (some $prefix in tokenize('${allowedPrefixes}', ',') satisfies matches(@Image, concat('^', $prefix))))
		)
	]
]
