rule.example:

// Violation: Variable name matches class name (case-insensitive)
public class Account {
    // ...
}

public void method() {
    Account Account = new Account();  // ❌ Variable name matches class
    Account ACCOUNT = new Account();  // ❌ Variable name matches class
}

// Valid: Use different variable name
public void method() {
    Account account = new Account();  // ✅ Different case
    Account acc = new Account();  // ✅ Different name
}


// Violation: Variable name matches inner class name
public class Outer {
    public class Inner {
        // ...
    }

    public void method() {
        Inner Inner = new Inner();  // ❌ Variable name matches inner class
    }
}

// Valid: Use different variable name
public void method() {
    Inner inner = new Inner();  // ✅
}
