rule.example:

// Violation: Trivial getter that only returns field
public class MyClass {
    private String name;

    public String getName() {  // ❌
        return name;
    }
}

// Valid: Use property syntax instead
public class MyClass {
    public String name { get; set; }  // ✅
}


// Valid: Getter with logic (not trivial)
public class MyClass {
    private String name;

    public String getName() {  // ✅ Has logic
        return name != null ? name.toUpperCase() : '';
    }
}

// Valid: Getter with conditional logic (not trivial)
public class MyClass {
    private Integer count;

    public Integer getCount() {  // ✅ Has logic
        if (count == null) {
            return 0;
        }
        return count;
    }
}


// Valid: Getter with method call (not trivial)
public class MyClass {
    private String value;

    public String getValue() {  // ✅ Has method call
        return value.trim();
    }
}

// Valid: Getter with loop (not trivial)
public class MyClass {
    private List<String> items;

    public List<String> getItems() {  // ✅ Has loop
        List<String> result = new List<String>();
        for (String item : items) {
            result.add(item.toUpperCase());
        }
        return result;
    }
}
